mapper.select:
select count(u1.user_id)
from `user` u1 join `user` u2  on (
    u1.solved>u2.solved or u1.solved=u2.solved and u1.submit<u2.submit or
    u1.solved=u2.solved and u1.submit=u2.submit and u1.user_id<u2.user_id
) and not u1.disabled where u2.user_id=#{id}

select user_id,nick,solved,submit from (
    (
        select u1.* from `user` u1 join `user` u2  on (
            u1.solved>u2.solved 
            or u1.solved=u2.solved and u1.submit<u2.submit
            or u1.solved=u2.solved and u1.submit=u2.submit and u1.user_id<u2.user_id
        ) and not u1.disabled
        where u2.user_id=#{id}
        order by u1.solved,u1.submit desc,u1.user_id desc limit #{c}
    ) union (
        select * from `user` where user_id=#{id}
    ) union (
        select u2.* from `user` u1 join `user` u2  on (
            u1.solved>u2.solved
            or u1.solved=u2.solved and u1.submit<u2.submit
            or u1.solved=u2.solved and u1.submit=u2.submit and u1.user_id<u2.user_id
        )  and not u2.disabled
        where u1.user_id=#{id} order by u2.solved desc,u2.submit,u2.user_id limit #{c}
    )
) z order by solved desc,submit,user_id

select
    temp.user_id,
    sol solved,
    sub submit,
    nick
from (
    select
        user_id,
        sum(if(score=100,1,0)) sol,
        count(*) sub
    from (
        select * from solution order by solution_id desc limit #{count}
    ) s group by user_id
    order by sol desc,sub asc limit 50
) temp,`user`
where temp.user_id=`user`.user_id

mapper.update:
update `user`
email=nullif(#{user.email},''), nick=#{user.nick}, school=#{user.school}, password=#{user.password}, modified_time=#{user.modifiedTime}, accesstime=#{user.accesstime}, ip=#{user.ip}, disabled=#{user.disabled},
user_id=#{userId}

